import { Component, Output, EventEmitter, Input } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { TranslationService } from '../../services/translation.service';
import { Lesson } from '../../models/lesson.model';

@Component({
  selector: 'app-add-lesson-modal',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './add-lesson-modal.component.html',
  styleUrls: ['./add-lesson-modal.component.css']
})
export class AddLessonModalComponent {
  @Input() isOpen = false;
  @Output() closeModal = new EventEmitter<void>();
  @Output() lessonAdded = new EventEmitter<Omit<Lesson, 'id'>>();

  portugueseText = '';
  isLoading = false;
  error = '';

  constructor(private translationService: TranslationService) {}

  onClose(): void {
    this.closeModal.emit();
    this.resetForm();
  }

  async onSubmit(): void {
    if (!this.portugueseText.trim()) {
      this.error = 'Por favor, digite um texto em português.';
      return;
    }

    this.isLoading = true;
    this.error = '';

    try {
      const englishText = await this.translationService.translateText({
        text: this.portugueseText.trim(),
        targetLanguage: 'en'
      }).toPromise();

      if (englishText) {
        const newLesson: Omit<Lesson, 'id'> = {
          portugueseText: this.portugueseText.trim(),
          englishText: englishText,
          level: 'Pessoal'
        };

        this.lessonAdded.emit(newLesson);
        this.onClose();
      } else {
        this.error = 'Não foi possível traduzir o texto. Tente novamente.';
      }
    } catch (error: any) {
      console.error('Translation error:', error);
      this.error = error.message || 'Erro ao traduzir o texto. Verifique sua conexão e tente novamente.';
    } finally {
      this.isLoading = false;
    }
  }

  private resetForm(): void {
    this.portugueseText = '';
    this.error = '';
    this.isLoading = false;
  }

  onOverlayClick(event: Event): void {
    if (event.target === event.currentTarget) {
      this.onClose();
    }
  }
}
<div 
  *ngIf="isOpen" 
  class="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50 p-4"
  (click)="onOverlayClick($event)">

  <div class="bg-white rounded-lg shadow-xl p-6 w-full max-w-md transform transition-all duration-300 scale-100">
    <!-- Header -->
    <div class="flex justify-between items-center mb-6">
      <h2 class="text-xl font-semibold text-gray-800">Adicionar Nova Lição</h2>
      <button 
        (click)="onClose()"
        class="p-1 hover:bg-gray-100 rounded-full transition-colors"
        [disabled]="isLoading">
        <svg class="w-6 h-6 text-gray-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M6 18L18 6M6 6l12 12"/>
        </svg>
      </button>
    </div>

    <!-- Form -->
    <form (ngSubmit)="onSubmit()" class="space-y-4">
      <!-- Input Field -->
      <div>
        <label for="portugueseText" class="block text-sm font-medium text-gray-700 mb-2">
          Texto em Português
        </label>
        <textarea
          id="portugueseText"
          [(ngModel)]="portugueseText"
          name="portugueseText"
          rows="4"
          class="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 resize-none"
          placeholder="Digite uma frase em português para ser traduzida..."
          [disabled]="isLoading"
          required>
        </textarea>
      </div>

      <!-- Error Message -->
      <div *ngIf="error" class="p-3 bg-red-50 border border-red-200 rounded-md">
        <p class="text-sm text-red-600">{{ error }}</p>
      </div>

      <!-- Info Message -->
      <div class="p-3 bg-blue-50 border border-blue-200 rounded-md">
        <p class="text-sm text-blue-600">
          <svg class="w-4 h-4 inline mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"/>
          </svg>
          O texto será automaticamente traduzido para inglês usando IA.
        </p>
      </div>

      <!-- Buttons -->
      <div class="flex justify-end space-x-3 pt-4">
        <button
          type="button"
          (click)="onClose()"
          [disabled]="isLoading"
          class="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-100 border border-gray-300 rounded-md hover:bg-gray-200 focus:outline-none focus:ring-2 focus:ring-gray-500 disabled:opacity-50 disabled:cursor-not-allowed transition-colors">
          Cancelar
        </button>

        <button
          type="submit"
          [disabled]="isLoading || !portugueseText.trim()"
          class="px-4 py-2 text-sm font-medium text-white bg-blue-600 border border-transparent rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 disabled:opacity-50 disabled:cursor-not-allowed transition-colors flex items-center">
          <svg *ngIf="isLoading" class="animate-spin -ml-1 mr-2 h-4 w-4 text-white" fill="none" viewBox="0 0 24 24">
            <circle class="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" stroke-width="4"></circle>
            <path class="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
          </svg>
          {{ isLoading ? 'Traduzindo...' : 'Adicionar Lição' }}
        </button>
      </div>
    </form>
  </div>
</div>
